// Simple Lightbox Component (function(){ const lb = document.createElement('div'); lb.id='lightbox-overlay'; lb.style.cssText='position:fixed;inset:0;background:rgba(0,0,0,.85);display:none;align-items:center;justify-content:center;z-index:1000;padding:2rem;'; lb.innerHTML='<button id="lb-close" style="position:absolute;top:1rem;right:1rem;font-size:1.5rem;color:#fff;background:none;border:none;cursor:pointer">âœ•</button><img id="lb-img" src="" alt="" style="max-width:100%;max-height:80vh;border-radius:12px;box-shadow:0 10px 30px rgba(0,0,0,.4)"/><div id="lb-caption" style="color:#eee;margin-top:1rem;text-align:center;font-family:Inter, sans-serif;font-size:.9rem"></div>'; document.addEventListener('DOMContentLoaded',()=>{document.body.appendChild(lb);}); function open(src,cap){ document.getElementById('lb-img').src=src; document.getElementById('lb-caption').textContent=cap||''; lb.style.display='flex'; } function close(){lb.style.display='none';} document.addEventListener('click',e=>{ if(e.target.matches('[data-lightbox]')){ e.preventDefault(); const el=e.target.closest('[data-lightbox]'); open(el.getAttribute('data-src')||el.src, el.getAttribute('data-caption')); }}); document.addEventListener('click',e=>{ if(e.target.id==='lb-close' || e.target===lb) close(); }); document.addEventListener('keyup',e=>{ if(e.key==='Escape') close(); }); })();